// Extract scraping parameters
const searchParams = {
  searchTerm: $input.all()[0].json.searchTerm || '',
  category: $input.all()[0].json.category || '',
  region: $input.all()[0].json.region || '',
  maxPages: $input.all()[0].json.maxPages || 5,
  dateFrom: $input.all()[0].json.dateFrom || '',
  dateTo: $input.all()[0].json.dateTo || '',
  generateSummary: $input.all()[0].json.generateSummary !== false, // Default true
  summaryLanguage: $input.all()[0].json.summaryLanguage || 'french' // french, arabic, english
};

return {
  ...searchParams,
  baseUrl: 'https://www.marchespublics.gov.ma',
  startTime: new Date().toISOString()
};

// Parse homepage and get session data
const cheerio = require('cheerio');
const html = $input.all()[0].json;
const $ = cheerio.load(html);

const formData = {};
$('input[type="hidden"]').each((i, elem) => {
  const name = $(elem).attr('name');
  const value = $(elem).attr('value');
  if (name && value) {
    formData[name] = value;
  }
});

const csrfToken = $('meta[name="csrf-token"]').attr('content') || 
                 $('input[name="_token"]').val();

const cookies = $input.all()[0].headers['set-cookie'] || [];
const cookieString = cookies.map(cookie => cookie.split(';')[0]).join('; ');

return {
  formData: formData,
  csrfToken: csrfToken,
  cookies: cookieString,
  searchUrl: 'https://www.marchespublics.gov.ma/index.php?page=entreprise.EntrepriseAvancee'
};

// Parse search results
const cheerio = require('cheerio');
const html = $input.all()[0].json;
const $ = cheerio.load(html);
const baseUrl = 'https://www.marchespublics.gov.ma';

const projects = [];

// Multiple selector strategies for different page layouts
const selectors = [
  '.projet, .annonce, .item-projet',
  'tr[class*="ligne"]',
  'table tr',
  '.list-item, .tender-item'
];

for (const selector of selectors) {
  if (projects.length > 0) break;

  $(selector).each((i, elem) => {
    if (i === 0 && selector.includes('tr')) return; // Skip header row

    const $elem = $(elem);

    // Try different patterns for title and link extraction
    let title = $elem.find('a[href*="detail"], .titre, .title').first().text().trim();
    let link = $elem.find('a[href*="detail"], a[href*="projet"]').first().attr('href');

    // Fallback for table-based layouts
    if (!title || !link) {
      const cells = $elem.find('td');
      if (cells.length >= 2) {
        title = $(cells[1]).find('a').text().trim() || $(cells[1]).text().trim();
        link = $(cells[1]).find('a').attr('href');
      }
    }

    if (title && link) {
      const fullLink = link.startsWith('http') ? link : baseUrl + '/' + link.replace(/^\//, '');

      projects.push({
        title: title,
        link: fullLink,
        description: $elem.find('.description, .resume, td:nth-child(3)').text().trim(),
        organisme: $elem.find('.organisme, .institution, td:nth-child(4)').text().trim(),
        datePublication: $elem.find('.date, td:nth-child(5)').text().trim(),
        montant: $elem.find('.montant, .budget, td:nth-child(6)').text().trim()
      });
    }
  });
}

const maxProjects = $('Extract Parameters').item.json.maxPages * 10;
return projects.slice(0, maxProjects).map(project => ({ json: project }));

// Parse detailed project information
const cheerio = require('cheerio');
const html = $input.all()[0].json;
const $ = cheerio.load(html);
const baseUrl = 'https://www.marchespublics.gov.ma';
const originalProject = $input.all()[1].json;

const projectDetail = {
  ...originalProject,
  fullDescription: '',
  specifications: '',
  deadline: '',
  attachments: [],
  criteria: '',
  contact: '',
  additionalInfo: {}
};

// Extract comprehensive project details
projectDetail.fullDescription = $('.description-complete, .detail-description, .contenu-projet, .content').text().trim();
projectDetail.specifications = $('.specifications, .cahier-charges, .conditions, .spec-tech').text().trim();
projectDetail.deadline = $('.date-limite, .echeance, .deadline, .date-cloture').text().trim();
projectDetail.criteria = $('.criteres, .conditions-participation, .criteria, .evaluation').text().trim();
projectDetail.contact = $('.contact, .coordonnees, .responsable, .info-contact').text().trim();

// Extract additional structured information
const additionalInfo = {};
$('.info-item, .field-item, .detail-item').each((i, elem) => {
  const $elem = $(elem);
  const label = $elem.find('.label, .field-label, dt').text().trim();
  const value = $elem.find('.value, .field-value, dd').text().trim();
  if (label && value) {
    additionalInfo[label] = value;
  }
});
projectDetail.additionalInfo = additionalInfo;

// Extract all document attachments
const attachments = [];
const fileExtensions = ['pdf', 'doc', 'docx', 'xls', 'xlsx', 'txt', 'zip', 'rar'];
const filePattern = new RegExp(`\\.(${fileExtensions.join('|')})$`, 'i');

$('a').each((i, elem) => {
  const $elem = $(elem);
  const href = $elem.attr('href');
  const text = $elem.text().trim();

  if (href && (filePattern.test(href) || href.includes('telecharger') || href.includes('download'))) {
    const fullUrl = href.startsWith('http') ? href : baseUrl + '/' + href.replace(/^\//, '');
    const fileType = href.match(filePattern) ? href.split('.').pop().toLowerCase() : 'unknown';

    attachments.push({
      name: text || `Document ${attachments.length + 1}`,
      url: fullUrl,
      type: fileType,
      size: null
    });
  }
});

projectDetail.attachments = attachments;

return projectDetail;

// Process attachments - download and extract content
const project = $input.all()[0].json;
const attachments = project.attachments.slice(0, 5); // Limit to 5 attachments per project

return attachments.map(attachment => ({
  json: {
    ...project,
    currentAttachment: attachment
  }
}));

// Extract text from various file types
const fileType = $input.all()[0].json.currentAttachment.type;
const fileBuffer = $input.all()[0].binary.data;
const projectData = $input.all()[0].json;

let extractedText = '';
let error = null;

try {
  switch (fileType) {
    case 'pdf':
      const pdfParse = require('pdf-parse');
      const pdfData = await pdfParse(fileBuffer);
      extractedText = pdfData.text;
      break;

    case 'docx':
    case 'doc':
      const mammoth = require('mammoth');
      const docResult = await mammoth.extractRawText({ buffer: fileBuffer });
      extractedText = docResult.value;
      break;

    case 'xlsx':
    case 'xls':
      const XLSX = require('xlsx');
      const workbook = XLSX.read(fileBuffer, { type: 'buffer' });
      let excelText = '';

      workbook.SheetNames.forEach(sheetName => {
        const sheet = workbook.Sheets[sheetName];
        const jsonData = XLSX.utils.sheet_to_json(sheet, { header: 1 });
        excelText += `\n=== Sheet: ${sheetName} ===\n`;
        jsonData.forEach(row => {
          if (row.length > 0) {
            excelText += row.join(' | ') + '\n';
          }
        });
      });
      extractedText = excelText;
      break;

    case 'txt':
      extractedText = fileBuffer.toString('utf-8');
      break;

    default:
      extractedText = 'Unsupported file type';
  }
} catch (err) {
  error = err.message;
  extractedText = '';
}

return {
  ...projectData,
  extractedContent: {
    fileName: projectData.currentAttachment.name,
    fileType: fileType,
    text: extractedText,
    error: error,
    extractedAt: new Date().toISOString()
  }
};

// Aggregate all project data and extracted content
const allData = $input.all();
const projectsMap = new Map();

// Group by project
allData.forEach(item => {
  const data = item.json;
  const projectKey = data.title + '_' + data.link;

  if (!projectsMap.has(projectKey)) {
    projectsMap.set(projectKey, {
      title: data.title,
      link: data.link,
      description: data.description,
      fullDescription: data.fullDescription,
      organisme: data.organisme,
      datePublication: data.datePublication,
      montant: data.montant,
      specifications: data.specifications,
      deadline: data.deadline,
      criteria: data.criteria,
      contact: data.contact,
      additionalInfo: data.additionalInfo || {},
      attachments: data.attachments || [],
      extractedContents: []
    });
  }

  // Add extracted content
  if (data.extractedContent) {
    projectsMap.get(projectKey).extractedContents.push(data.extractedContent);
  }
});

// Convert to array and prepare for summarization
const projects = Array.from(projectsMap.values()).map(project => {
  // Combine all text content
  const allTexts = [
    project.description,
    project.fullDescription,
    project.specifications,
    project.criteria,
    ...project.extractedContents.map(content => 
      `=== ${content.fileName} ===\n${content.text}`
    )
  ].filter(text => text && text.trim()).join('\n\n');

  return {
    ...project,
    combinedText: allTexts,
    totalAttachments: project.attachments.length,
    processedAttachments: project.extractedContents.length,
    readyForSummary: true
  };
});

return projects.map(project => ({ json: project }));

