// Create final comprehensive response with summary
const allProjects = $input.all();
const summaryData = allProjects.find(item => item.json.choices);
const projectsData = allProjects.filter(item => !item.json.choices);

// Extract AI summary if available
const aiSummary = summaryData?.json.choices?.[0]?.message?.content || '';

// Calculate statistics
const totalProjects = projectsData.length;
const totalBudget = projectsData.reduce((sum, item) => {
  const montant = item.json.montant || '';
  const numericValue = parseFloat(montant.replace(/[^0-9.,]/g, '').replace(',', '.'));
  return sum + (isNaN(numericValue) ? 0 : numericValue);
}, 0);

const organizationStats = {};
const sectorStats = {};

projectsData.forEach(item => {
  const project = item.json;

  // Count by organization
  const org = project.organisme || 'Non spécifié';
  organizationStats[org] = (organizationStats[org] || 0) + 1;

  // Analyze sectors based on title keywords
  const title = project.title.toLowerCase();
  let sector = 'Autres';

  if (title.includes('informatique') || title.includes('système') || title.includes('logiciel')) {
    sector = 'Informatique';
  } else if (title.includes('construction') || title.includes('bâtiment') || title.includes('travaux')) {
    sector = 'Construction';
  } else if (title.includes('fourniture') || title.includes('équipement') || title.includes('matériel')) {
    sector = 'Fournitures';
  } else if (title.includes('service') || title.includes('prestation') || title.includes('conseil')) {
    sector = 'Services';
  } else if (title.includes('santé') || title.includes('médical') || title.includes('hospitalier')) {
    sector = 'Santé';
  } else if (title.includes('éducation') || title.includes('formation') || title.includes('enseignement')) {
    sector = 'Éducation';
  }

  sectorStats[sector] = (sectorStats[sector] || 0) + 1;
});

// Create executive summary
const executiveSummary = {
  totalProjects: totalProjects,
  totalEstimatedBudget: totalBudget,
  topOrganizations: Object.entries(organizationStats)
    .sort(([,a], [,b]) => b - a)
    .slice(0, 5)
    .map(([org, count]) => ({ organization: org, projectCount: count })),
  sectorDistribution: Object.entries(sectorStats)
    .sort(([,a], [,b]) => b - a)
    .map(([sector, count]) => ({ sector, count, percentage: Math.round((count / totalProjects) * 100) })),
  upcomingDeadlines: projectsData
    .filter(item => item.json.deadline)
    .map(item => ({
      title: item.json.title,
      deadline: item.json.deadline,
      organisme: item.json.organisme
    }))
    .slice(0, 10)
};

// Prepare final response
const response = {
  success: true,
  searchSummary: {
    searchTerm: $('Extract Parameters').item.json.searchTerm,
    category: $('Extract Parameters').item.json.category,
    region: $('Extract Parameters').item.json.region,
    searchDate: new Date().toISOString()
  },
  executiveSummary: executiveSummary,
  aiAnalysis: aiSummary,
  projects: projectsData.map(item => ({
    ...item.json,
    // Remove combined text from individual projects to reduce response size
    combinedText: undefined
  })),
  metadata: {
    totalProjects: totalProjects,
    processedAt: new Date().toISOString(),
    processingTime: 'Calculated by workflow'
  }
};

return response;

// Handle projects without attachments
const project = $input.all()[0].json;

return {
  ...project,
  extractedContents: [],
  combinedText: [project.description, project.fullDescription, project.specifications].filter(Boolean).join('\n\n'),
  totalAttachments: 0,
  processedAttachments: 0,
  readyForSummary: true
};

